import React, { useState } from 'react';
import { Router, Link } from '@reach/router';
import TodoModel from './TodoModel';
import useNetlifyIdentity from 'hooks/useNetlifyIdentity';
import { useInput } from 'hooks';
import { ModelCtx, UserCtx } from 'contexts';
import { LoggedIn } from './components/LoggedIn';
import './App.css';
import './login.css';
const model = new TodoModel('react-todos');

let Home = () => <div>Home2</div>;
const NotFound = () => <div>Sorry, nothing here.</div>;

function Login() {
  const { user, doLogin } = React.useContext(UserCtx);
  return (
    <div className="Login">
      {user ? (
        <LoggedIn />
      ) : (
        <span>
          <a onClick={doLogin}>Login or Sign Up</a>
        </span>
      )}
    </div>
  );
}

function List(props) {
  const { model, lists } = React.useContext(ModelCtx);
  const list = model.list();
  return (
    <div>
      <h3>List</h3>
      <div className="listNav">
        <label>{list.data.title}</label>
        <button onClick={() => alert('oops')}>back to all lists</button>
      </div>
      <pre>{JSON.stringify(props, null, 2)}</pre>
    </div>
  );
}
function AllLists() {
  const { lists } = React.useContext(ModelCtx);
  console.log('---', { lists });
  return (
    <div>
      <div className="listNav">
        <label>Choose a list.</label>
      </div>
      <section className="main">
        <ul className="todo-list">
          {lists.map(({ data, ref }) => {
            console.log('list', data, ref);
            return (
              <li key={ref.value.id}>
                <label onClick={() => alert('go')}>{data.title}</label>
              </li>
            );
          })}
        </ul>
      </section>
    </div>
  );
}

function InputArea() {
  const { model, lists } = React.useContext(ModelCtx);
  const { setValue, ...inputProps } = useInput();
  const handleNewTodoKeyDown = event => {
    if (event.keyCode !== 13) return;
    event.preventDefault();
    var val = event.target.value.trim();
    if (val) {
      model.addList(val);
      // if (this.state.nowShowing === ALL_LISTS) {
      //   modelRef.current.addList(val);
      // } else {
      //   modelRef.current.addTodo(val, modelRef.current.list());
      // }
      setValue('');
    }
  };
  return (
    <input
      className="new-todo"
      placeholder="Create a new list or choose from below."
      onKeyDown={handleNewTodoKeyDown}
      {...inputProps}
      autoFocus={true}
    />
  );
}

export default function App(props) {
  const modelRef = React.useRef(new TodoModel('react-todos'));
  const model = modelRef.current;
  const identity = useNetlifyIdentity(modelRef.current.onAuthChange);
  const { user, doLogout, doLogin, authedFetch } = identity;
  const [lists, setLists] = React.useState(model.lists);
  React.useEffect(() => {
    model.subscribe(() => {
      setLists(model.lists);
    });
  }, []);
  return (
    <ModelCtx.Provider value={{ model, lists }}>
      <UserCtx.Provider value={identity}>
        <div>
          <header className="header">
            <h1>todos</h1>
            <Login />
            <Router>
              <AllLists path="/" />
              <div path="list">
                <List path=":listId" />
                <List path=":listId/active" />
                <List path=":listId/completed" />
              </div>
              <NotFound default />
            </Router>
            <InputArea />
          </header>
          <footer className="footer">footer</footer>
        </div>
      </UserCtx.Provider>
    </ModelCtx.Provider>
  );
}
